    },
    initOffset() {
      let $sel = $("#navUL li.selected");
      let index = $sel.index();
      if (index > 0) index = index - 1;
      let left = 1;
      for (var i = 0; i < index; i++) {
        left += $("#navUL li")
          .eq(index)
          .outerWidth(true);
      }
      $("#navPanelDiv").animate({ scrollLeft: left }, 200);
    },
    changeWidth() {
      let w = 0;
